package mx.com.cinepolis.scheduler.service.impl;

import mx.com.cinepolis.scheduler.commons.to.GitHubUserTO;
import mx.com.cinepolis.scheduler.commons.to.LogeoTO;
import mx.com.cinepolis.scheduler.service.GitHubServices;

public class GitHubServiceImpl implements GitHubServices {

	@Override
	public GitHubUserTO getDataGitHubUser(String log) {
		GitHubUserTO gitHubUser = new GitHubUserTO();
		gitHubUser.setLogin(log);
		gitHubUser.setName("Alberto");
		gitHubUser.setAvatar_url("data:image/png;base64,");
		gitHubUser.setFollowers(5);
		gitHubUser.setFollowing(10);
		return gitHubUser;
	}

	@Override
	public LogeoTO login(String user, String pass) {
		LogeoTO logeoTO = new LogeoTO();
		if(user.equals("jairo") && pass.equals("123")) {			
			logeoTO.setUser(user);
			logeoTO.setPassword(pass);
		}else {
			logeoTO.setUser("Usuario Incorrecto");
			logeoTO.setPassword("Password Incorrecto");
		}
			
		return logeoTO;
	}

	@Override
	public GitHubUserTO register(GitHubUserTO gitHubUserTO) {
		GitHubUserTO gitHubUser = new GitHubUserTO();
		gitHubUser.setLogin(gitHubUserTO.getLogin());
		gitHubUser.setName(gitHubUserTO.getName());
		gitHubUser.setAvatar_url(gitHubUserTO.getAvatar_url());
		gitHubUser.setFollowers(gitHubUserTO.getFollowers());
		gitHubUser.setFollowing(gitHubUserTO.getFollowing());
		
		return gitHubUser;
	}
	
	
  
}

